/*定义*/
@keyframes showAPP {
    0% {
        opacity: 0;
        margin-left: -300px;
    }

    100% {
        opacity: .8;
        margin-left: 3px;
    }
}

@keyframes showtop {
    0% {
        opacity: 0;
        margin-left: -100px;
    }

    100% {
        opacity: 1;
        margin-left: 0px;
    }
}

@keyframes showUI {
    0% {
        opacity: 0;
        margin-top: -50px;
    }

    100% {
        opacity: 1;
        margin-top: 20px;
    }
}

@keyframes slowshow {
    0% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}

@keyframes clan {
    0% {
        margin-left: -50px;
    }
}
